        fun setExampleTranslations(list: List<Collocation>) {
            for (i in list) {
                i.translations = ArrayList<String>()
                for (s in i.examples) {
                    (i.translations as ArrayList<String>).add("Translation is <b>downloading</b>")
                }
            }
        }

        fun getListOfCollocationCat(relation: String): ArrayList<Collocation> {
            var collocations2 = arrayListOf<Collocation>()
            var collocations: ArrayList<Collocation>
            try {
                collocations =
                    Gson().fromJson(
                        getStringCat(),
                        object : TypeToken<List<Collocation?>?>() {}.type
                    )
                collocations.get(0).isChecked = true
                collocations.get(1).isChecked = true

                for (c in collocations) {
                    if (c.relation.equals(relation)) collocations2.add(c)
                }

            } catch (e: Exception) {
                return ArrayList<Collocation>()
            }
            return collocations
        }
    }

