            return collocations
        }

        fun setExampleTranslations(list: List<Collocation>) {
            for (i in list) {
                i.translations = ArrayList<String>()
                for (s in i.examples) {
                    (i.translations as ArrayList<String>).add("Translation is downloading")
                }
            }
        }

